mapper.select:
select cbwla.serial_no, cbwla.chain_coin_no, c3.coin, c3.chain_name, cbwla.address, cbwla.status, cbwla.type, cbwla.remark, cbwla.tenant_id, cp.platform_name, cbwla.create_by, cbwla.create_time, cbwla.update_by, cbwla.update_time from waas_black_white_list_address as cbwla left join waas_chain_coin as c3 on cbwla.chain_coin_no = c3.serial_no and c3.del_flag = 0 and c3.status = 1 left join crm_platform as cp on cbwla.tenant_id = cp.tenant_id and cp.del_flag = 0
and cbwla.chain_coin_no = #{dto.chainCoinNo}
and c3.coin = #{dto.coin}
and c3.chain_name = #{dto.chainName}
and cbwla.status = #{dto.status}
and cbwla.address = #{dto.address}
and cbwla.create_time >= #{dto.startTime}
and cbwla.create_time <= #{dto.endTime}
and cbwla.del_flag = 0

select cbwla.serial_no, cbwla.chain_coin_no, c3.coin, c3.chain_name, cbwla.address, cbwla.status, cbwla.type, cbwla.remark, cbwla.tenant_id, cp.platform_name, cbwla.create_by, cbwla.create_time, cbwla.update_by, cbwla.update_time from crm_black_white_list_address as cbwla left join crm_chain_coin as c3 on cbwla.chain_coin_no = c3.serial_no and c3.del_flag = 0 and c3.status = 1 left join crm_platform as cp on cbwla.tenant_id = cp.tenant_id and cp.del_flag = 0
and cbwla.chain_coin_no = #{dto.chainCoinNo}
and c3.coin = #{dto.coin}
and c3.chain_name = #{dto.chainName}
and cbwla.status = #{dto.status}
and cbwla.address = #{dto.address}
and cbwla.create_time >= #{dto.startTime}
and cbwla.create_time <= #{dto.endTime}
and cbwla.del_flag = 0
