mapper.select:
select c3.serial_no, c3.chain_coin_key, c3.chain_coin_name, c3.short_name, c3.coin, c3.chain_name, c3.contract_address, c3.coin_decimal, c3.unit, c3.status, c3.type, c3.remark, c3.logo_url from waas_customer_chain_coin as c4 left join waas_chain_coin as c3 on c3.serial_no = c4.chain_coin_no and c4.del_flag = '0'
and c4.create_role_account_flag = #{params.createRoleAccountFlag}
and c4.create_user_account_flag = #{params.createUserAccountFlag}
and c4.biz_role_type_no = #{params.bizRoleTypeNo}
and c4.biz_role_type = #{params.bizRoleType}
and c4.tenant_id = #{params.tenantId} and c4.del_flag = '0'

select c3.chain_coin_key as chainCoinKey, c3.chain_coin_name as chainCoinName, c3.short_name as shortName, c3.coin as coin, c3.chain_name as chainName, c4.serial_no as serialNo, c4.chain_coin_no as chainCoinNo, c4.create_role_account_flag as createRoleAccountFlag, c4.create_user_account_flag as createUserAccountFlag, c4.business_role_type as businessRoleType, c4.business_role_no as businessRoleNo, c4.tenant_id as tenantId, c4.create_by as createBy, c4.create_time as createTime from crm_customer_chain_coin as c4 left join crm_chain_coin as c3 on c3.serial_no = c4.chain_coin_no and c3.del_flag = '0'
and c3.coin = #{params.coin}
and c3.chain_coin_key = #{params.chainCoinKey}
and c3.chain_coin_name like concat ('%',#{params.chainCoinName},'%')
and c4.create_role_account_flag = #{params.createRoleAccountFlag}
and c4.create_user_account_flag = #{params.createUserAccountFlag}
and c4.biz_role_type_no = #{params.bizRoleTypeNo}
and c4.biz_role_type = #{params.bizRoleType}
and c4.create_time >= #{params.startTime}
and c4.create_time <= #{params.endTime}
and c4.tenant_id = #{params.tenantId} and c3.status = 1 and c4.del_flag = '0'
order by c4.create_time desc
